mapper.select:
SELECT * FROM bd_srch_info
WHERE report_no = #{report_no}
ORDER BY report_stats DESC, report_no DESC, submit_dt DESC, report_cre_dt DESC

mapper.update:
UPDATE bd_srch_info
user_id = #{user_id}, business_nm = #{business_nm}, object_place_nm = #{object_place_nm}, object_relation_user = #{object_relation_user}, object_relation_user_home_tel = #{object_relation_user_home_tel}, object_relation_user_mobil_tel = #{object_relation_user_mobil_tel}, object_location = #{object_location}, object_use = #{object_use}, object_buil_struc = #{object_buil_struc}, check_period_start = #{check_period_start}, check_period_end = #{check_period_end}, check_user_nm = #{check_user_nm}, check_user_qualfication_class = #{check_user_qualfication_class}, check_user_qualfication_no = #{check_user_qualfication_no}, submit_dt = #{submit_dt}, head_fire_depart = #{head_fire_depart}, location_lat = #{location_lat}, location_lon = #{location_lon}, report_cre_dt = #{report_cre_dt}, report_stats = #{report_stats}
WHERE report_no = #{report_no}

UPDATE bd_srch_info
check_extgsh_kind=#{check_extgsh_kind}, check_extgsh_result=#{check_extgsh_result}, check_alarm_equip_kind=#{check_alarm_equip_kind}, check_alarm_equip_result=#{check_alarm_equip_result}, check_extgsh_equip_kind=#{check_extgsh_equip_kind}, check_extgsh_equip_result=#{check_extgsh_equip_result}, check_flee_equip_kind=#{check_flee_equip_kind}, check_flee_equip_result=#{check_flee_equip_result}, check_etc_equip_kind=#{check_etc_equip_kind}, check_etc_equip_result=#{check_etc_equip_result}, special_note=#{special_note}, submit_dt = NOW(), report_stats = '2'
WHERE report_no = #{report_no}
