mapper.select:
   	 	SELECT count(*)
		  FROM bd_user_info
         WHERE user_id     = #{user_id}

		SELECT *
		  FROM bd_user_info
		 WHERE user_id       = #{user_id}
		   	  
           	 AND user_pw	 = #{user_pw}
           
           
       	     AND user_otg_no = #{user_otg_no}

mapper.update:
UPDATE bd_user_info
   
   		user_nm=#{user_nm}, 
   		user_appve_yn=#{user_appve_yn},
   		user_role=#{user_role},
   		user_otg_no=#{user_otg_no}
   
 WHERE user_id = #{user_id}

UPDATE bd_user_info
    
   		user_appve_yn=#{user_appve_yn},
   		user_role=#{user_role},
   		user_otg_no=#{user_otg_no}
   
 WHERE user_id = #{user_id}

UPDATE bd_user_info
   SET 
       user_pw  = #{user_pw}
 WHERE user_id  = #{user_id}
